(function(){const t=document.getElementById("subscription-form"),e=document.getElementById("form-status"),n=t.querySelector('button[type="submit"]');t.addEventListener("submit",function(s){s.preventDefault(),n.disabled=!0,e.textContent="",e.className="status-message",fetch("https://website-backend.p-v.workers.dev/subscribers",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:document.getElementById("email-input").value,referrer:window.location.pathname})}).then(s=>{s.ok?(e.textContent="Thanks for subscribing!",t.querySelector(".form-group").style.display="none"):(e.textContent="Something went wrong. Please try again.",n.disabled=!1)}).catch(t=>{console.error("Error:",t),e.textContent="Something went wrong. Please try again.",n.disabled=!1})})})()